import React,{useEffect, useState} from 'react';


const Prop = () => {
  
  const [count,setCount]=useState(1);
  const [incount,setincount]=useState(1);
  
  useEffect(()=>{
    var timer = setInterval(()=>{
      
      setCount(count+1);
      if(count==10*incount){
        setincount(incount+1);
        
      }

      
      
      
     
    },1000);
    return () => clearInterval(timer);
  
  })
  
  return (
    <>
       <div className="">
        <div className="flex flex-col justify-center items-center min-h-screen">
        <div className="flex gap-6">
        <button className="rounded-lg py-5 px-5 bg-yellow-500 ">Decrement</button>
        <h1 className="font-bold text-">{count}</h1>
        
        <button className="rounded-lg py-5 px-5 bg-green-500">Increment</button>
        </div>
        
        
  <button
            className="rounded-lg px-11 py-11 m-3 border-4 border-gray-950 
            bg-blue-500"
   >
            {incount}
          </button>
</div>
</div>
    </>
  )
}

export default Prop
