import React, { useEffect, useState } from 'react';

const Prop = () => {
  const [count, setCount] = useState(1);
  const [incount, setIncount] = useState(5);

  useEffect(() => {
    const timer = setInterval(() => {
      setCount(prevCount => {
        if (prevCount === 10) {
          clearInterval(timer);
          return prevCount;
        }
        return prevCount + 1;
      });
    }, 1000);

    return () => clearInterval(timer);
  }, []);

  const handleDecrement = () => {
    setIncount(prevIncount => prevIncount - 1);
  };

  const handleIncrement = () => {
    setIncount(prevIncount => prevIncount + 1);
  };

  return (
    <>
      <div className="flex flex-col justify-center items-center min-h-screen">
        <div className="flex gap-7">
          <button className="rounded-lg py-5 px-5 bg-green-500" onClick={handleDecrement}>
            Decrement
          </button>
          <h1>{count}</h1>
          <button className="rounded-lg py-5 px-5 bg-yellow-500" onClick={handleIncrement}>
            Increment
          </button>
        </div>
        
        <button
          className={`rounded-lg px-11 py-11 m-3 border-4 border-gray-500 ${
            count === 10 ? 'bg-red-500' : 'bg-blue-500'
          }`}
        >
          {incount}
        </button>
      </div>
    </>
  );
};

export default Prop;
