    var timer = setInterval(()=>{
      count
    },1000)
  })
  
  return (
    <>
       <div className="">
        <div className="flex flex-col justify-center items-center min-h-screen">
        <div className="flex gap-7">
        <button className="rounded-lg py-5 px-5 bg-green-950 ">Decrement</button>
        <button className="rounded-lg py-5 px-5 bg-yellow-950">Increment</button>
        </div>
        
        
  <button onClick={()=>{counti(count+1);}} className="bg-blue-500 rounded-lg px-11 py-11 m-3 border-4 border-gray-950">{count}</button>
</div>
</div>
    </>
  )
}

