import React,{useState} from 'react';


const Prop = () => {
  
  const [count,counti]=useState(1);
  const inc=()=>{
    counti(count+1);

  }
  return (
    <>
       
        <div className="flex flex-col justify-center items-center min-h-screen">
        <div className="flex ">
        <button className="rounded-lg py-5 px-5 bg-green-950">Decrement</button>
        <button className="rounded-lg py-5 px-5 bg-green-950">Increment</button>
        </div>
        
        <h1 className="font-bold text-3xl">{count}</h1>
  <button onClick={inc} className="bg-blue-500 rounded-lg px-11 py-11 m-3 border-4 border-gray-950">{count}</button>
</div>
    </>
  )
}

export default Prop
